INSERT INTO POST (content, is_active, post_date, title, visibility, author, category)
VALUES
('Kinh doanh hiện đại đang chuyển mình với sự phát triển của các công nghệ số. Các doanh nghiệp cần áp dụng những giải pháp công nghệ để nâng cao hiệu quả và năng suất.', 1, '2025-02-04 18:00:00', 'Kinh doanh trong thời đại số', 1, 6, 11),
('Các môn thể thao như bóng đá, bóng rổ không chỉ giúp giải trí mà còn có tác dụng rất tốt đối với sức khỏe. Chúng ta cần duy trì một lối sống thể thao để luôn khỏe mạnh.', 1, '2025-02-03 13:10:00', 'Tại sao thể thao quan trọng?', 1, 4, 4),
('Một buổi sáng đẹp trời, bạn thức dậy và thưởng thức một cốc cà phê nóng. Đó chính là cảm giác tuyệt vời mà cà phê mang lại cho bạn mỗi ngày.', 1, '2025-02-01 09:00:00', 'Sự tuyệt vời của một cốc cà phê', 1, 8, 9),
('Phim ảnh là một phương tiện nghệ thuật tuyệt vời giúp chúng ta giải trí và học hỏi. Đặc biệt, các bộ phim tài liệu mang lại nhiều kiến thức bổ ích về thế giới xung quanh.', 1, '2025-02-02 16:30:00', 'Khám phá phim tài liệu', 1, 7, 7),
('Những người yêu thích du lịch sẽ không thể bỏ qua những địa điểm du lịch tuyệt vời trên thế giới. Từ những bãi biển đến những thành phố cổ kính, mỗi địa điểm đều có những điều thú vị để khám phá.', 1, '2025-02-05 08:30:00', 'Top các địa điểm du lịch nổi bật', 1, 9, 8),
('Sức khỏe là tài sản quý giá nhất của mỗi con người. Việc duy trì một lối sống lành mạnh và ăn uống khoa học sẽ giúp bạn sống khỏe mạnh lâu dài.', 1, '2025-01-30 11:40:00', 'Duy trì sức khỏe qua chế độ ăn uống hợp lý', 1, 10, 10),
('Thời trang không chỉ là cách ăn mặc, mà còn là sự thể hiện cá tính. Một bộ trang phục phù hợp sẽ giúp bạn tự tin và nổi bật trong mọi hoàn cảnh.', 1, '2025-02-03 12:20:00', 'Phong cách thời trang năm 2025', 1, 13, 13),
('Bóng đá luôn là môn thể thao vua. Đội tuyển nào chiến thắng không chỉ là niềm tự hào mà còn là niềm vui cho hàng triệu người hâm mộ trên toàn thế giới.', 1, '2025-01-31 17:45:00', 'Bóng đá - Niềm đam mê của hàng triệu người', 1, 4, 4),
('Trong thế giới hiện đại, việc học qua mạng đã trở nên rất phổ biến. Với các khóa học trực tuyến, bạn có thể học bất cứ lúc nào, ở bất kỳ đâu.', 1, '2025-02-04 14:15:00', 'Học qua mạng – Lợi ích và thách thức', 1, 5, 3),
('Kinh doanh trực tuyến đang phát triển mạnh mẽ. Các cửa hàng online không chỉ phục vụ nhu cầu mua sắm mà còn là một phần của xu hướng tiêu dùng hiện đại.', 1, '2025-02-01 10:30:00', 'Kinh doanh trực tuyến - Xu hướng mới', 1, 6, 11),
('Du lịch là một trải nghiệm tuyệt vời để bạn khám phá các nền văn hóa, phong tục, ẩm thực mới. Đi du lịch không chỉ mang lại niềm vui mà còn giúp bạn mở rộng tầm nhìn.', 1, '2025-01-30 15:00:00', 'Du lịch và những trải nghiệm đáng nhớ', 1, 8, 8),
('Bóng đá luôn là môn thể thao hấp dẫn, không chỉ mang lại những trận đấu kịch tính mà còn tạo ra những cơ hội cho các cầu thủ thể hiện tài năng.', 1, '2025-01-27 12:10:00', 'Bóng đá - Những trận đấu huyền thoại', 1, 4, 4),
('Một số chiến lược học tập hiệu quả mà sinh viên nên áp dụng để đạt được kết quả học tập tốt. Ngoài việc học bài, bạn cũng cần có kế hoạch ôn tập phù hợp.', 1, '2025-01-29 10:00:00', 'Chiến lược học tập hiệu quả cho sinh viên', 1, 5, 3),
('Sức khỏe là vấn đề rất quan trọng đối với mỗi người. Ngoài việc tập luyện, bạn cũng cần chú ý đến chế độ dinh dưỡng để duy trì sức khỏe lâu dài.', 1, '2025-02-05 10:00:00', 'Chế độ dinh dưỡng hợp lý cho sức khỏe', 1, 10, 10),
('Các bộ phim hành động luôn thu hút người xem bởi sự kịch tính, những pha hành động mãn nhãn. Đặc biệt, các bộ phim Hollywood luôn có một lượng fan đông đảo.', 1, '2025-01-28 17:50:00', 'Khám phá các bộ phim hành động nổi bật', 1, 7, 7),
('Lịch sử và văn hóa luôn có mối liên hệ chặt chẽ với nhau. Những di sản văn hóa có thể được khám phá qua những câu chuyện lịch sử của các nền văn minh.', 1, '2025-02-02 08:40:00', 'Khám phá lịch sử và văn hóa của các quốc gia', 1, 15, 15),
('Nhiếp ảnh không chỉ là nghệ thuật mà còn là cách để ghi lại những khoảnh khắc đáng nhớ trong cuộc sống. Mỗi bức ảnh đều có câu chuyện riêng.', 1, '2025-02-04 11:00:00', 'Nhiếp ảnh - Nghệ thuật ghi lại khoảnh khắc', 1, 20, 20),
('Tự học là một trong những cách tốt nhất để phát triển bản thân. Bạn có thể học qua sách, video, hoặc tham gia các khóa học online để nâng cao kỹ năng.', 1, '2025-02-01 14:40:00', 'Lợi ích của việc tự học', 1, 16, 16),
('Kinh doanh sáng tạo đang thu hút nhiều nhà đầu tư. Các ý tưởng sáng tạo không chỉ giúp tạo ra sản phẩm mới mà còn thay đổi cách thức làm việc và tiêu dùng.', 1, '2025-02-03 16:20:00', 'Kinh doanh sáng tạo - Xu hướng mới', 1, 6, 11),
('Công nghệ đã thay đổi cách chúng ta học. Các ứng dụng học tập trực tuyến giúp việc học trở nên dễ dàng và hiệu quả hơn bao giờ hết.', 1, '2025-01-31 09:30:00', 'Công nghệ và cách mạng giáo dục', 1, 5, 3),
('Chế độ ăn uống đóng vai trò quan trọng trong việc duy trì sức khỏe. Những thói quen ăn uống lành mạnh sẽ giúp bạn có một cơ thể khỏe mạnh và tinh thần sảng khoái.', 1, '2025-02-02 13:25:00', 'Chế độ ăn uống lành mạnh', 1, 10, 10),
('Thị trường game hiện nay đang phát triển rất nhanh. Các tựa game mới ra đời luôn thu hút sự chú ý của cộng đồng game thủ, đặc biệt là game online.', 1, '2025-01-28 12:00:00', 'Game online - Cộng đồng và xu hướng mới', 1, 18, 18),
('Phim ảnh là một phần quan trọng trong cuộc sống giải trí. Các thể loại phim từ hành động đến lãng mạn đều có một lượng khán giả riêng.', 1, '2025-02-04 14:10:00', 'Các thể loại phim và khán giả', 1, 7, 7),
('Sự phát triển của thời trang không chỉ thể hiện qua các bộ sưu tập mà còn thể hiện qua sự thay đổi trong cách nhìn nhận về cái đẹp.', 1, '2025-02-01 13:15:00', 'Sự phát triển của thời trang trong những năm qua', 1, 13, 13),
('Kinh doanh trực tuyến là một lựa chọn tuyệt vời cho các nhà đầu tư. Bạn có thể bắt đầu từ những ý tưởng đơn giản và phát triển chúng theo cách của riêng mình.', 1, '2025-02-02 10:40:00', 'Kinh doanh trực tuyến - Những điều cần biết', 1, 6, 11),
('Một bức ảnh có thể kể lại hàng nghìn câu chuyện. Nhiếp ảnh giúp ghi lại khoảnh khắc đẹp của cuộc sống và giúp chúng ta nhìn nhận thế giới qua một lăng kính mới.', 1, '2025-02-04 16:50:00', 'Nhiếp ảnh - Khoảnh khắc đẹp trong cuộc sống', 1, 20, 20);
